#!/usr/bin/python

from aigen_common import execute_hwtool_query

def get_kernel_cmdline():
    try:
        hwt_kernel_opts = execute_hwtool_query('kernel-opts')
    except:
        hwt_kernel_opts = ''

    try:
        from rbt_product_settings import get_prod_kernel_options
        prod_kernel_opts = get_prod_kernel_options()
    except ImportError:
        prod_kernel_opts = ''

    if prod_kernel_opts != '':
        prod_kernel_opts = ' ' + prod_kernel_opts

    console_opts='console=tty0 console=ttyS0,9600n8'

    kernel_opts = '%s%s %s' % (hwt_kernel_opts, prod_kernel_opts, console_opts)
    return kernel_opts
